const day_0_weather = document.getElementById('day_0_weather');
const weather_of_day_0 = document.getElementById('weather_of_day_0');
const temp_of_day_0 = document.getElementById('temp_of_day_0');

const day_1_weather = document.getElementById('day_1_weather');
const weather_of_day_1 = document.getElementById('weather_of_day_1');
const temp_of_day_1 = document.getElementById('temp_of_day_1');

const day_2_weather = document.getElementById('day_2_weather');
const weather_of_day_2 = document.getElementById('weather_of_day_2');
const temp_of_day_2 = document.getElementById('temp_of_day_2');

const day_3_weather = document.getElementById('day_3_weather');
const weather_of_day_3 = document.getElementById('weather_of_day_3');
const temp_of_day_3 = document.getElementById('temp_of_day_3');

const day_4_weather = document.getElementById('day_4_weather');
const weather_of_day_4 = document.getElementById('weather_of_day_4');
const temp_of_day_4 = document.getElementById('temp_of_day_4');

let api = 'e2a7eb8fb47f6546ba6b8d79c8e4d9cd';
let city = 'Salt Lake City, UT';
let country = 'US';
let url = `http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&units=imperial&APPID=${api}`;

let clear_sky_url = "data:image/jpeg;base64,";
let few_clouds_url = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR5BUd_FP71xutOE8Z7Ctyaao0mzNglssGbkGvkPmzt2j3jR92je-tqBWgbFQ&s";
let scattered_clouds_url = "data:image/jpeg;base64,";
let broken_clouds_url = "data:image/jpeg;base64,";
let overcast_clouds_url = "data:image/jpeg;base64,";
let shower_rain_url = "data:image/jpeg;base64,"
let rain_url = "data:image/jpeg;base64,";
let thunderstorm_url = "data:image/jpeg;base64,";
let snow_url = "data:image/jpeg;base64,";
let mist_url = "data:image/jpeg;base64,";
let weather_url;

let get_weather_url = weather_description => {
  switch (weather_description) {
    case "clear sky":
      weather_url = clear_sky_url;
      break;
    case "few clouds":
      weather_url = few_clouds_url;
      break;
    case "scattered clouds":
      weather_url = scattered_clouds_url;
      break;
    case "broken clouds":
      weather_url = broken_clouds_url;
      break;
    case "overcast clouds":
      weather_url = overcast_clouds_url;
      break;
    case "shower rain":
      weather_url = shower_rain_url;
      break;
    case "rain":
      weather_url = rain_url;
      break;
    case "thunderstorm":
      weather_url = thunderstorm_url;
      break;
    case "snow":
      weather_url = snow_url;
      break;
    case "mist":
      weather_url = mist_url;
      break;
    
  }
}



fetch(url)
  .then(response => response.json())
  .then(data => {
    console.log(data);
    
    get_weather_url(data.list[0].weather[0].description);
    day_0_weather.style.backgroundImage = 'url(' + weather_url + ')';
    weather_of_day_0.innerHTML = data.list[0].weather[0].description;
    temp_of_day_0.innerHTML = data.list[0].main.temp + ' &deg;F';

    get_weather_url(data.list[8].weather[0].description);
    day_1_weather.style.backgroundImage = 'url(' + weather_url + ')';
    weather_of_day_1.innerHTML = data.list[8].weather[0].description;
    temp_of_day_1.innerHTML = data.list[8].main.temp + ' &deg;F';
    
    get_weather_url(data.list[16].weather[0].description);
    day_2_weather.style.backgroundImage = 'url(' + weather_url + ')';
    weather_of_day_2.innerHTML = data.list[16].weather[0].description;
    temp_of_day_2.innerHTML = data.list[16].main.temp + ' &deg;F';
    
    get_weather_url(data.list[24].weather[0].description);
    day_3_weather.style.backgroundImage = 'url(' + weather_url + ')';
    weather_of_day_3.innerHTML = data.list[24].weather[0].description;
    temp_of_day_3.innerHTML = data.list[24].main.temp + ' &deg;F';
    
    get_weather_url(data.list[32].weather[0].description);
    day_4_weather.style.backgroundImage = 'url(' + weather_url + ')';
    weather_of_day_4.innerHTML = data.list[32].weather[0].description;
    temp_of_day_4.innerHTML = data.list[32].main.temp + ' &deg;F';
})
  .catch(e => console.log(e));